import React, { useEffect, useRef } from 'react';

function MyForm() {
  const captchaRef = useRef();



  const handleSubmit = (e) => {
    e.preventDefault();

    const captchaSolution = captchaRef.current?.value;

    if (captchaSolution) {
      alert('Captcha solved! Solution: ' + captchaSolution);
    } else {
      alert('Please complete the captcha.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Enter your name" required />

      <div
        className="frc-captcha"
        data-sitekey="FCMGI6QFS6T1IESL" // Site Key صحیح
      >
        <noscript>Please enable JavaScript to use the captcha.</noscript>
      </div>
      <input type="hidden" ref={captchaRef} name="frc-captcha-solution" />

      <button type="submit">Submit</button>
    </form>
  );
}

export default MyForm;
