import React, { useState, useEffect } from "react";
import { createCanvas } from "canvas";

const MathCaptcha = () => {
  const [captchaImage, setCaptchaImage] = useState(null);
  const [captchaAnswer, setCaptchaAnswer] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");

  // تولید اعداد و عملیات به‌صورت رندوم
  const generateCaptcha = () => {
    const num1 = Math.floor(Math.random() * 10); // عدد اول
    const num2 = Math.floor(Math.random() * 10); // عدد دوم
    const operations = ["+", "-", "*"];
    const operation = operations[Math.floor(Math.random() * operations.length)]; // عملیات
    let answer;

    switch (operation) {
      case "+":
        answer = num1 + num2;
        break;
      case "-":
        answer = num1 - num2;
        break;
      case "*":
        answer = num1 * num2;
        break;
      default:
        answer = 0;
    }

    setCaptchaAnswer(answer);
    return { num1, num2, operation };
  };

  // تولید عکس Captcha
  const createCaptchaImage = ({ num1, num2, operation }) => {
    const canvas = createCanvas(200, 70);
    const ctx = canvas.getContext("2d");
    // پس‌زمینه
    ctx.fillStyle = "#f3f3f3";
    ctx.fillRect(0, 0, 200, 70);

    // متن Captcha
    ctx.font = "30px Arial";
    ctx.fillStyle = "#333";
    ctx.fillText(`${num1} ${operation} ${num2} = ?`, 40, 45);
// پس‌زمینه ساده با رنگ گرادیان
const gradient = ctx.createLinearGradient(0, 0, 200, 70);
gradient.addColorStop(0, "#f3f3f3");
gradient.addColorStop(1, "#d3d3d3");
ctx.fillStyle = gradient;
ctx.fillRect(0, 0, 200, 70);

    // تبدیل کانواس به URL
    const dataURL = canvas.toDataURL();
    return dataURL;
  };

  // وقتی صفحه لود شد یا Captcha رفرش شد
  useEffect(() => {
    const captcha = generateCaptcha();
    const image = createCaptchaImage(captcha);
    setCaptchaImage(image);
  }, []);

  // بررسی جواب کاربر
  const handleSubmit = (e) => {
    e.preventDefault();
    if (parseInt(userAnswer, 10) === captchaAnswer) {
      alert("جواب درست است!");
    } else {
      alert("جواب اشتباه است. دوباره امتحان کنید.");
    }
  };

  // رفرش کردن Captcha
  const refreshCaptcha = () => {
    const captcha = generateCaptcha();
    const image = createCaptchaImage(captcha);
    setCaptchaImage(image);
    setUserAnswer("");
  };

  return (
    <section className="w-[200px] mx-auto mt-[100px]">
      {captchaImage && <img src={captchaImage} alt="captcha" />}
      <form onSubmit={handleSubmit} className="flex flex-col justify-center gap-4">
        <input
          type="number"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          placeholder="پاسخ را وارد کنید"
          required

          className=" p-[5px] text-base border rounded-md w-[200px] mt-4"
        />
        <section className="flex justify-between items-center">


      <button
        onClick={refreshCaptcha}
        className="py-1 px-2 bg-orange-300 rounded-md font-semibold"
      >
        رفرش
      </button>
      <button type="submit" className="py-1 px-2 bg-lime-600 rounded-md font-semibold">
          ارسال
        </button>
        </section>
      </form>
    </section>
  );
};

export default MathCaptcha;
