import React, { useState, useEffect } from "react";
import { createCanvas } from "canvas";

const MathCaptcha = () => {
  const [captchaImage, setCaptchaImage] = useState(null);
  const [captchaAnswer, setCaptchaAnswer] = useState(null);
  const [userAnswer, setUserAnswer] = useState("");

  // تولید اعداد و عملیات به‌صورت رندوم
  const generateCaptcha = () => {
    const num1 = Math.floor(Math.random() * 10); // عدد اول
    const num2 = Math.floor(Math.random() * 10); // عدد دوم
    const operations = ["+", "-", "*"];
    const operation = operations[Math.floor(Math.random() * operations.length)]; // عملیات
    let answer;

    switch (operation) {
      case "+":
        answer = num1 + num2;
        break;
      case "-":
        answer = num1 - num2;
        break;
      case "*":
        answer = num1 * num2;
        break;
      default:
        answer = 0;
    }

    setCaptchaAnswer(answer);
    return { num1, num2, operation };
  };

  // تولید عکس Captcha
  const createCaptchaImage = ({ num1, num2, operation }) => {
    const canvas = createCanvas(200, 70);
    const ctx = canvas.getContext("2d");

    // پس‌زمینه
    ctx.fillStyle = "#f3f3f3";
    ctx.fillRect(0, 0, 200, 70);

    // متن Captcha
    ctx.font = "30px Arial";
    ctx.fillStyle = "#333";
    ctx.fillText(`${num1} ${operation} ${num2} = ?`, 40, 45);

    // تبدیل کانواس به URL
    const dataURL = canvas.toDataURL();
    return dataURL;
  };

  // وقتی صفحه لود شد یا Captcha رفرش شد
  useEffect(() => {
    const captcha = generateCaptcha();
    const image = createCaptchaImage(captcha);
    setCaptchaImage(image);
  }, []);

  // بررسی جواب کاربر
  const handleSubmit = (e) => {
    e.preventDefault();
    if (parseInt(userAnswer, 10) === captchaAnswer) {
      alert("جواب درست است!");
    } else {
      alert("جواب اشتباه است. دوباره امتحان کنید.");
    }
  };

  // رفرش کردن Captcha
  const refreshCaptcha = () => {
    const captcha = generateCaptcha();
    const image = createCaptchaImage(captcha);
    setCaptchaImage(image);
    setUserAnswer("");
  };

  return (
    <section className="w-[300px] mx-auto my-auto">
      {captchaImage && <img src={captchaImage} alt="captcha" />}
      <form onSubmit={handleSubmit}>
        <input
          type="number"
          value={userAnswer}
          onChange={(e) => setUserAnswer(e.target.value)}
          placeholder="پاسخ را وارد کنید"
          required
          style={{ margin: "10px", padding: "5px", fontSize: "16px" }}
        />
        <button type="submit" style={{ padding: "5px 10px", fontSize: "16px" }}>
          ارسال
        </button>
      </form>
      <button
        onClick={refreshCaptcha}
        style={{ marginTop: "10px", padding: "5px 10px", fontSize: "16px" }}
      >
        رفرش
      </button>
    </section>
  );
};

export default MathCaptcha;
