import React, { useState } from 'react';

function SimpleCaptcha() {
  const [captcha, setCaptcha] = useState('');
  const [userInput, setUserInput] = useState('');
  const [error, setError] = useState('');

  // تولید کپچای تصادفی
  const generateCaptcha = () => {
    const randomCaptcha = Math.random().toString(36).substring(2, 8).toUpperCase();
    setCaptcha(randomCaptcha);
    setError('');
  };

  // اعتبارسنجی کپچا
  const handleSubmit = (e) => {
    e.preventDefault();
    if (userInput === captcha) {
      alert('Captcha Verified Successfully!');
      setUserInput('');
      generateCaptcha();
    } else {
      setError('Captcha is incorrect. Please try again.');
    }
  };

  // تولید کپچا در بارگذاری اولیه
  React.useEffect(() => {
    generateCaptcha();
  }, []);

  return (
    <form onSubmit={handleSubmit}>
      <p>Enter the text shown below:</p>
      <div style={{ fontSize: '24px', fontWeight: 'bold', margin: '10px 0' }}>
        {captcha || 'Loading...'}
      </div>
      <input
        type="text"
        placeholder="Enter Captcha"
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
        required
      />
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <button type="submit">Submit</button>
    </form>
  );
}

export default SimpleCaptcha;
