import React, { useEffect, useRef } from 'react';
import { Helmet } from 'react-helmet'; // Assuming you're using React Helmet for script management

function App() {
  const captchaScriptRef = useRef(null);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://unpkg.com/friendly-challenge@0.9.11/widget.module.min.js';
    script.async = true; // Optionally set async for parallel loading

    script.onerror = () => {
      console.error('Failed to load Friendly Captcha script');
      // Implement fallback behavior or error handling here
    };

    captchaScriptRef.current = script;
    document.body.appendChild(script);

    return () => {
      if (captchaScriptRef.current) {
        document.body.removeChild(captchaScriptRef.current);
      }
    };
  }, []); // Empty dependency array for script loading on initial mount

  return (
    <div>
      <Helmet>
        {/* Add script with nomodule attribute here if necessary based on browser support */}
      </Helmet>
      <h1>Friendly Captcha Example</h1>
      <div className="frc-captcha" data-sitekey="FCMGI6QFS6T1IESL" data-callback="onCaptchaSolved">
        <noscript>Please enable JavaScript to use the captcha.</noscript>
      </div>
    </div>
  );
}

export default App;
