import React, { useState } from 'react';

function MyForm() {
  const [isVerified, setIsVerified] = useState(false);
  const [captchaSolution, setCaptchaSolution] = useState('');

  const handleCaptchaSolved = (solution) => {
    setCaptchaSolution(solution); // ذخیره پاسخ کپچا
    setIsVerified(true); // تائید اینکه کپچا تکمیل شده است
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (isVerified) {
      alert('Form submitted successfully!');
      // ارسال فرم به سرور به همراه `captchaSolution`
    } else {
      alert('Please complete the captcha before submitting.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Enter your name" required />
      <FriendlyCaptchaWidget
        sitekey="YOUR_SITE_KEY" // جایگزین با Site Key دریافتی
        doneCallback={handleCaptchaSolved} // تابع برای وقتی که کاربر کپچا را کامل می‌کند
      />
      <button type="submit" disabled={!isVerified}>
        Submit
      </button>
    </form>
  );
}

export default MyForm;
