import React, { useEffect } from 'react';

function App() {
  useEffect(() => {
    const script1 = document.createElement('script');
    script1.src = 'https://unpkg.com/friendly-challenge@0.9.11/widget.module.min.js';
    script1.type = 'module';
    document.body.appendChild(script1);

    const script2 = document.createElement('script');
    script2.src = 'https://unpkg.com/friendly-challenge@0.9.11/widget.min.js';
    script2.setAttribute('nomodule', '');
    script2.defer = true;
    document.body.appendChild(script2);

    return () => {
      // حذف اسکریپت‌ها هنگام خروج از کامپوننت
      document.body.removeChild(script1);
      document.body.removeChild(script2);
    };
  }, []);

  return (
    <div>
      <h1>Friendly Captcha Example</h1>
      {/* ویجت کپچا */}
      <div
        className="frc-captcha"
        data-sitekey="YOUR_SITE_KEY"
        data-callback="onCaptchaSolved"
      >
        <noscript>Please enable JavaScript to use the captcha.</noscript>
      </div>
    </div>
  );
}

export default App;
